+// /api/reminder.js
+import connectDB, { Event } from "../lib/db.js";
+import Job from "../models/job.js";
+import nodemailer from "nodemailer";
+
+const transporter = nodemailer.createTransport({
+  service: "gmail",
+  auth: { user: process.env.ADMIN_EMAIL, pass: process.env.ADMIN_PASS },
+});
+
+async function sendEmail(to, title, date) {
+  return transporter.sendMail({
+    from: process.env.ADMIN_EMAIL,
+    to,
+    subject: `Reminder: "${title}" event in 5 minutes`,
+    text: `Hello,\n\nThis is a reminder for your event: "${title}" scheduled at ${date}.\n\n- Event Dashboard`,
+  });
+}
+
+export default async function handler(req, res) {
+  if (req.method !== "GET") return res.status(405).json({ msg: "Method not allowed" });
+
+  await connectDB();
+
+  const now = new Date();
+  //const fiveMinutesLater = new Date(now.getTime() + 5 * 60000);
+  const fiveMinutesLater = new Date(now.getTime() + 2 * 60000);
+
+  const events = await Event.find({
+    date: { $lte: fiveMinutesLater, $gte: now },
+  });
+
+  let processed = 0;
+
+  for (const event of events) {
+    try {
+      if (event.status === "CONFIRMED") {
+        // Send reminder email
+        await sendEmail(event.createdBy, event.title, event.date);
+        // Create Job with SENT
+        await Job.create({
+          eventId: event._id,
+          createdOn: new Date().toISOString(),
+          sentTo: event.createdBy,
+          status: "SENT",
+        });
+      } else if (event.status === "DELETED" || event.status === "CANCELLED") {
+        // Create Job with FAILED
+        await Job.create({
+          eventId: event._id,
+          createdOn: new Date().toISOString(),
+          sentTo: event.createdBy,
+          status: "FAILED",
+          motifFailure: event.status,
+        });
+      }
+      processed++;
+    } catch (err) {
+      console.error(`Failed to process event ${event._id}:`, err);
+      // Create Job with FAILED for any errors
+      await Job.create({
+        eventId: event._id,
+        createdOn: new Date().toISOString(),
+        sentTo: event.createdBy,
+        status: "FAILED",
+        motifFailure: err.message,
+      });
+    }
+  }
+
+  return res.json({ processed });
+}

